    "dojo/dom-attr",
    "dojo/string",
    "dojo/topic",
    "dojo/query",
    "dojo/on",
    "dojox/html/entities",
    
    // Grid X
    "gridx/Grid",
    "gridx/core/model/cache/Sync",
    "gridx/modules/select/Row",
    "gridx/modules/ColumnResizer",
    "gridx/modules/CellWidget",
