  "dojo/i18n!webui/nls/web",
  "dojo/_base/array",
  "dojo/_base/declare", 
  "dojo/_base/lang",
  "dojo/dom",
  "dojo/dom-attr",
  "dojo/dom-class",
  "dojo/dom-construct",
  "dojo/dom-geometry",
  "dojo/hash", 
  "dojo/on",
  "dojo/query",
  "dojo/request/xhr",
  "dojo/topic",
  "dojox/html/entities",
  "dijit/_TemplatedMixin", 
  "dijit/_WidgetBase", 
  "dijit/_WidgetsInTemplateMixin",
  "dijit/form/Button",
  "dijit/layout/ContentPane",
  "dijit/registry",
  "dijit/TitlePane",  
  "webui/config/Resources",
  "webui/utils/RestUri",
  "webui/utils/WebUiHash",
  "webui/widgets/common/UIUtils",
  "webui/widgets/common/AttributesStore",
  "webui/widgets/common/D3FlowDiagram",
  "webui/widgets/common/Trace",
  "webui/widgets/policy/MessageDialog",
